import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './App.css';

function App() {
  const HeaderButton = ({children}) => (
    <div className="header-button-container">
      <button className="header-button">{children}</button>
    </div>
  );

  // Lista de rutas de imágenes para el carrusel
  const carouselImages = [
    '/imagenes/producto1.jpg',
    '/imagenes/producto2.jpg',
    '/imagenes/producto3.jpg',
    // Agrega más rutas de imágenes aquí si es necesario
  ];

  const settings = {
    infinite: false,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
    swipeToSlide: true,
  };

  return (
    <div className="app">
      <header className="header">
        <div className="app-name">SixHearts</div>
        <div className="header-buttons">
          <div className="header-buttons-left">
            <div className="header-button-small header-button-dark">Inicio</div>
            <div className="header-button-small header-button-dark">Sneakers</div>
          </div>
          <HeaderButton>Buscar...</HeaderButton>
          <HeaderButton>Mi carrito</HeaderButton>
          <HeaderButton>Mi cuenta</HeaderButton>
        </div>
      </header>
      <main className="main-content">
        <div className="presentation">
          <h1 className="title">Six Hearts</h1>
          <img className="presentation-image" src={process.env.PUBLIC_URL + '/imagenes/foto_sn.jpg'} alt="Presentación" />
        </div>
        <div className="products">
          <h2 className="title">Nuestros productos</h2>
          <Slider {...settings}>
            {/* Iterar sobre la lista de rutas de imágenes y renderizar cada imagen */}
            {carouselImages.map((image, index) => (
              <div key={index}>
                <img src={process.env.PUBLIC_URL + image} alt={`Producto ${index + 1}`} />
              </div>
            ))}
          </Slider>
        </div>
      </main>
    </div>
  );
}

export default App;
