import React, { useState } from 'react';
import './App.css';

function App() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const carouselImages = [
    '/client/public/imagenes/adidas-Campus-00s-Core-Black-Product.jpg',
    '/client/public/imagenes/adidas-Campus-00s-Grey-White-Product.jpg',
    '/client/public/imagenes/adidas-Yeezy-Boost-350-V2-Onyx-Product.jpg '
  ];

  const handlePrevClick = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === 0 ? carouselImages.length - 1 : prevIndex - 1));
  };

  const handleNextClick = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === carouselImages.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className="app">
      {/* Resto del c√≥digo del encabezado y contenido principal */}
      <main className="main-content">
        {/* Resto del contenido principal */}
        <div className="products">
          <h2 className="title">Nuestros productos</h2>
          <div className="carousel">
            <img src={process.env.PUBLIC_URL + carouselImages[currentImageIndex]} alt={`Producto ${currentImageIndex + 1}`} />
            <div className="navigation-buttons">
              <button onClick={handlePrevClick}>Anterior</button>
              <button onClick={handleNextClick}>Siguiente</button>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
