import React, { useState } from 'react';
import './Navbar.css';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';

const Navbar = () => {
    const [menu, setMenu] = useState('shop');
    const [showMenu, setShowMenu] = useState(false);

    return (
    <div className="navbar">
        <div className="nav-logo">
        <img src={logo} alt="logo" />
        <p>SixHearts</p>
        </div>
        <ul className="nav-menu">
        <li onClick={() => setMenu('inicio')}>
            Inicio
            {menu === 'inicio' ? <hr /> : <></>}
        </li>
        <li onClick={() => setMenu('pago')}>
          Pago
          {menu === 'pago' ? <hr /> : <></>}
        </li>
        <li onClick={() => setShowMenu(!showMenu)}>
          Sneakers
          {showMenu && <MarcasMenu setShowMenu={setShowMenu} />}
        </li>
      </ul>
      <div className="nav-login-cart">
        <button>Login</button>
        <img src={cart_icon} alt="" />
        <div className="nav-cart-count">0</div>
      </div>
    </div>
  );
};

const MarcasMenu = ({ setShowMenu }) => {
  return (
    <ul className="marcas-menu">
      <li onClick={() => setShowMenu(false)}>Adidas</li>
      <li onClick={() => setShowMenu(false)}>Nike</li>
      <li onClick={() => setShowMenu(false)}>Jordan</li>
      <li onClick={() => setShowMenu(false)}>New Balance</li>
    </ul>
  );
};

export default Navbar;
