import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';
import { Link, useHistory } from 'react-router-dom';

export const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const history = useHistory();

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:3000/api/auth/login', {
                correo_electronico: email,
                contraseÑa: password
            });
            console.log(response.data);
            // Aquí puedes guardar el token en el almacenamiento local y redirigir al usuario
            localStorage.setItem('token', response.data.token);
            history.push('/nueva-pagina'); // Redirige a la nueva página en caso de éxito
        } catch (error) {
            console.error(error.response.data);
            setError(error.response.data.error); // Establece el mensaje de error
        }
    }

    return (
        <div className='login-container'>
            <form onSubmit={handleSubmit}>
                <label>
                    Email:
                    <input type="email" value={email} onChange={e => setEmail(e.target.value)} />
                </label>

                <label>
                    Contrasena:
                    <input type="password" value={password} onChange={e => setPassword(e.target.value)} />
                </label>
                
                <input type="submit" value="Iniciar sesión" />
            </form>
            {error && <p>{error}</p>} {/* Muestra el mensaje de error si hay uno */}
            <p>No tienes una cuenta? <Link to="/signup">Crea una</Link></p>
        </div>
    );
}

export default Login;
