import React, { useContext, useState, useEffect } from 'react';
import { CartContext } from '../Context/CartContext'; // Importa el CartContext
import ProductCard from '../Components/ProductCard/ProductCard';
import './CSS/Cart.css'; // Importa el archivo CSS

const Cart = () => {
    const [cart] = useContext(CartContext); // Accede al contexto del carrito
    const [firstProduct, setFirstProduct] = useState(null); // Estado local para almacenar el primer producto del carrito

    // Calcula el precio total
    const totalPrice = cart.reduce((total, product) => total + product.price, 0);

    // Actualiza el primer producto cuando cambie el carrito
    useEffect(() => {
        if (cart.length > 0) {
            setFirstProduct(cart[0]);
        } else {
            setFirstProduct(null);
        }
    }, [cart]);

    return (
        <div className="cart-page">
            <div className='espaciado'>
                <h1>Mi carrito:</h1>
                <h1>RESUMEN DE COMPRA:</h1>
            </div>

            {cart.length === 0 ? ( // Verifica si el carrito está vacío
                <div className='espaciado'>
                    <p>¡Empieza un carrito de compras!</p>
                    <p style={{fontSize: '13px'}}>Aquí verás información de tu compra una vez que agregues productos.</p>
                </div>
            ) : (
                <>
                    {firstProduct && (
                        <div className='espaciado'>
                            <p>{firstProduct.name}</p> {/* Muestra el nombre del primer producto */}
                            {/* Aquí puedes mostrar cualquier otra información relevante del primer producto */}
                        </div>
                    )}
                    {cart.map(product => (
                        <ProductCard key={product.id} product={product} inCart />
                    ))}
                    <h2>Total: ${totalPrice}</h2> {/* Muestra el precio total */}
                </>
            )}
        </div>
    );
};

export default Cart;
