// Components/ProductDetails/ProductDetails.jsx

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './ProductDetails.css'; // Importa tus estilos CSS

const ProductDetail = () => {
  // Utiliza el hook useParams para obtener el ID del producto de la URL
    const { id } = useParams();

  // Aquí podrías usar un estado local para almacenar los detalles del producto
    const [product, setProduct] = useState(null);

  // Simula la carga de los detalles del producto usando useEffect
    useEffect(() => {
    // Aquí deberías hacer una solicitud a tu API o usar datos locales para obtener los detalles del producto
    // Por ahora, simplemente simularemos una solicitud con un objeto de datos
    const fetchProductDetail = async () => {
      // Simulamos una solicitud a una API para obtener los detalles del producto
        const response = await fetch(`https://api.example.com/products/${id}`);
        const data = await response.json();
      // Actualiza el estado con los detalles del producto
        setProduct(data);
    };

    fetchProductDetails(); // Llamada a la función de simulación de solicitud de producto
}, [id]);

  // Renderiza los detalles del producto
    return (
    <div className="product-details">
        {product ? (
        <div>
            <h2>{product.name}</h2>
            <img src={product.image} alt={product.name} />
            <p>Categoría: {product.category}</p>
            <p>Precio: ${product.price}</p>
          {/* Otros detalles del producto */}
        </div>
        ) : (
        <p>Cargando detalles del producto...</p>
    )}
    </div>
);
};

export default ProductDetails;
