import { useContext } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../../Context/CartContext';
import './ProductCard.css'

const ProductCard = ({ product }) => {
    const [carti, setCart] = useContext(CartContext); // Accede al contexto del carrito

    const addToCart = () => { // Crea la función addToCart
        setCart(currentCart => [...currentCart, product]);
    };

    return (
        <div className='product-card'>
            <Link to={`/product/${product.id}`}>
                <img src={product.image} alt={product.name} />
                <h2>{product.name}</h2>
                <p>{product.category}</p>
                <p>${product.price}</p>
            </Link>
            <p><button onClick={addToCart}>Añadir al carrito</button></p> {/* Añade un manejador de clics al botón */}
        </div>
    );
};

export default ProductCard;
