import React, { useContext } from 'react';
import { CartContext } from '../Context/CartContext'; // Importa el CartContext
import './CSS/Cart.css'

const Cart = () => {
    const [cart] = useContext(CartContext); // Accede al contexto del carrito

    return (
        <div>
            <h1>Tu carrito:</h1>
            {cart.map(product => (
                // Aquí puedes renderizar cada producto en el carrito
                <div key={product.id}>
                    <h2>{product.name}</h2>
                    <p>{product.category}</p>
                    <p>${product.price}</p>
                </div>
            ))}
        </div>
    );
};

export default Cart;
