import React, { useState } from 'react';
import './Navbar.css';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';

const Navbar = () => {
  const [menu, setMenu] = useState('shop');

  const DropdownButton = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [selectedOption, setSelectedOption] = useState(null);

    const toggleDropdown = () => {
      setIsOpen(!isOpen);
    };

    const selectOption = (option) => {
      setSelectedOption(option);
      setIsOpen(false);
    };

    return (
      <div className="dropdown">
        <button className="dropdown-toggle" onClick={toggleDropdown}>
          {selectedOption ? selectedOption : 'Seleccionar marca'}
        </button>
        {isOpen && (
          <div className="dropdown-menu">
            <button className="dropdown-item" onClick={() => selectOption('Adidas')}>
              Adidas
            </button>
            <button className="dropdown-item" onClick={() => selectOption('Nike')}>
              Nike
            </button>
            <button className="dropdown-item" onClick={() => selectOption('Jordan')}>
              Jordan
            </button>
            <button className="dropdown-item" onClick={() => selectOption('New Balance')}>
              New Balance
            </button>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="navbar">
      <div className="nav-logo">
        <img src={logo} alt="logo" />
        <p>SixHearts</p>
      </div>
      <ul className="nav-menu">
        <li onClick={() => { setMenu('inicio') }}>Inicio{menu === "inicio" ? <hr /> : <></>} </li>
        <li onClick={() => { setMenu('pago') }}>Pago{menu === "pago" ? <hr /> : <></>} </li>
        <li>
          <DropdownButton />
        </li>
      </ul>
      <div className="nav-login-cart">
        <button>Login</button>
        <img src={cart_icon} alt="" />
        <div className="nav-cart-count">0</div>
      </div>
    </div>
  );
}

export default Navbar;
