import React, { useState } from 'react';
import './Navbar.css';
import { Link } from 'react-router-dom';
import logo from '../Assets/logo.png';
import cart_icon from '../Assets/cart_icon.png';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className='navbar'>
      <div className='nav-logo'>
        <img src={logo} alt='logo' />
        <p>SixHearts</p>
      </div>
      <ul className='nav-menu'>
        <li>
          <Link to='/' className={menuOpen ? 'active' : ''}>
            Inicio
          </Link>
        </li>
        <li>
          <Link to='/pago' className={menuOpen ? 'active' : ''}>
            Pago
          </Link>
        </li>
        <li className={menuOpen ? 'dropdown active' : 'dropdown'}>
          <span onClick={toggleMenu}>Sneakers</span>
          <div className={`dropdown-content ${menuOpen ? 'show' : ''}`}>
            <Link to='/adidas' onClick={() => setMenuOpen(false)}>Adidas</Link>
            <Link to='/nike' onClick={() => setMenuOpen(false)}>Nike</Link>
            <Link to='/newbalance' onClick={() => setMenuOpen(false)}>New Balance</Link>
            <Link to='/jordan' onClick={() => setMenuOpen(false)}>Jordan</Link>
          </div>
        </li>
      </ul>
      <div className='nav-login-cart'>
        <button>Login</button>
        <img src={cart_icon} alt='' />
        <div className='nav-cart-count'>0</div>
      </div>
    </div>
  );
};

export default Navbar;
